let all_letras = document.querySelector('#cont_all_lyric');
let reload_fundo = document.querySelector('#fundoreload');
let seg_fundo = 500;

function fundo_reload(){
    if (reload_fundo.innerHTML == '<i class="fa-solid fa-rotate-right"></i>'){
        reload_fundo.innerHTML = '<i class="fa-solid fa-xmark"></i>';
        reload_fundo.style.background = '#ec3833';
        reload_fundo.style.color = 'white';
        track.addEventListener("timeupdate", bg,false);
        function bg() {
            if(this.currentTime > this.duration-10){
                fade_out();
                if(this.currentTime > this.duration-8){
                track.currentTime = seg_fundo;
                setTimeout(fade_in, 0.001);
                }
            } 
        }
    } else {
        reload_fundo.innerHTML = '<i class="fa-solid fa-rotate-right"></i>';
        reload_fundo.style.background = 'white';
        reload_fundo.style.color = 'black';
        track.onended = function() {
            pausesong();
        }
    }
}
var fadeTimer = false;

function fade_in() {
    clearTimeout(fadeTimer);
    if (track.volume < 1) {
        track.volume += 0.01;
        fadeTimer = setTimeout(fade_in,50)
    } else {
        track.volume = 1;
        track.play;
    }
};

function fade_out() {
    clearTimeout(fadeTimer);
    if (track.volume > 0.01) {
        track.volume -= 0.01;
        fadeTimer = setTimeout(fade_out,15);
    } else {
        track.volume = 0.01;
        track.pause;
    }
};


